import { getHeroById, getHeroByOwner } from '../src/bases/08-imp-exp'

const getHeroByIdAsync = (id) => {

    return new Promise((resolve, reject) => {
        
        let heroe;

        setTimeout(() => {
            heroe = getHeroById(id);
        }, 2000);

        if (heroe !== undefined) {
            resolve(heroe);
        }
        else {
            reject();
        }

    });

}

getHeroByIdAsync(1)
    .then(elem => console.log(elem))
    .catch((error) => console.log('Hubo un error al encontrar al heroe'));
