import { getHeroById, getHeroByOwner } from '../src/bases/08-imp-exp'

const getHeroByIdAsync = (id) => {

    return new Promise((resolve, reject) => {
        
        setTimeout(() => {
            const heroe = getHeroById(id);

            if (heroe) {
                resolve(heroe);
            }
            else {
                reject('No se pudo encontrar al heroe');
            }

        }, 2000);

    });

}

getHeroByIdAsync(7)
    .then(console.log)
    .catch(console.warn);
