// Usando el operador ternario y versión aún más simplificada.

let isActive = true;

// Condición con operador ternario (if y else).
const mensaje = isActive ? 'Activo' : 'Inactivo';

let mensaje2 = isActive && 'Activo';
console.log(mensaje2);
