import React, { useState } from 'react';

// Componente Funcional.

const GifExpertApp = (props) => {

    const [categories, setCategories] = useState(['The Beatles', 'The Who', 'The Doors', 'The Rolling Stones']);

    // Primera forma de agregar categorías
    //  const addCategory = () => setCategories([...categories, 'Pink Floyd']);

    // Segunda forma.
    const addCategory = () => setCategories(elems => [...elems, 'Pink Floyd']);

    return (
      <>
            <h2>GifExpertApp</h2>
            <hr></hr>
            <ol>
                {
                    categories.map( (category) => {
                        return <li key={category}>{category}</li>;
                    })
                }
            </ol>

            <button onClick={ addCategory }>Agregar</button>
        </>
    );

}

export default GifExpertApp;
