import React, { useState, useEffect } from 'react'
import GifGridItem from './GifGridItem'

const GifGrid = ({ category }) => {

    const [images, setImages] = useState([]);
    useEffect(() => {
        getGifs();
    }, [])

    const getGifs = async () => {
        const url = 'https://api.giphy.com/v1/gifs/search?q=Beatles&limit=10&api_key=kxjhjPQCp5ByXjj8hIlj0P8YkqRKHxz4';
        const answer = await fetch(url);
        const { data } = await answer.json();

        const gifs = data.map(img => {
            return {
                id: img.id,
                title: img.title,
                url: img.images?.downsized_medium.url
            }
        })

        console.log(gifs);
        setImages(gifs);
    }

    return (
        <div>
            <h3>{category}</h3>
           
            {
                images.map((img) => (
                    <GifGridItem
                        img={img}
                        key={img.id}
                    />
                ))
            }
            
        </div>
    )
}

export default GifGrid;
