        getGifs();
    }, [])

    const getGifs = async () => {
        const url = 'https://api.giphy.com/v1/gifs/search?q=Beatles&limit=10&api_key=kxjhjPQCp5ByXjj8hIlj0P8YkqRKHxz4';
        const answer = await fetch(url);
        const { data } = await answer.json();

        const gifs = data.map(img => {
            return {
                id: img.id,
                title: img.title,
                url: img.images?.downsized_medium.url
            }
        })

        console.log(gifs);
    }

    return (
        <div>
            <h3>{category}</h3>
