import { useEffect, useState } from "react"
import getGifs from '../helpers/getGifs'

const useFetchGifs = () => {

    const [state, setState] = useState({
        data: [],
        loading: true
    })

    useEffect(() => {
        getGif
    }, [])

    return state; // Retorna el objeto.

}

export default useFetchGifs;
