import React, { useEffect, useReducer } from 'react';
import { todoReducer } from './todoReducer'
import { useForm } from '../../hooks/useForm';
import { TodoListGroup } from './TodoListGroup'
import './styles.css';

const init = () => {

    // Si hay objetos a parsear los devuelve, si no, solo retorna un arreglo vacío para el estado inicial.
    return JSON.parse(localStorage.getItem('todos')) || [];

}

export const TodoApp = () => {

   const [todos, dispatch] = useReducer(todoReducer, [], init);

   const [{description}, handleInputChange, reset] = useForm({
        description: ''
   });

    useEffect(() => {
        localStorage.setItem('todos', JSON.stringify(todos));
    }, [todos])

    const handleDelete = (todoId) => {
        const action = {
            type: 'delete',
            payload: todoId
        }

        dispatch(action);
    }

    const handleToggle = (todoId) => {

        dispatch({
            type: 'toggle',
            payload: todoId
        })

    }

    const handleSubmit = (evt) => {
        evt.preventDefault();

        if (description.trim().length <= 1) {
            return;
        }

        const newTodo = {
            id: new Date().getTime(),
            desc: description,
            done: false
        }

        const action = {
            type: 'add',
            payload: newTodo
        }

        dispatch(action);
        reset();
    }

    return (
        <div>
            <h1>Todo App ({ todos.length })</h1>
            <hr />

            <div className="row">

                <div className="col-7">

                    <TodoListGroup todos={todos} handleToggle={ handleToggle } handleDelete={ handleDelete } />

                        {/* <ul className="list-group list-group-flush">
                        {
                            todos.map( (todo, i) => (
                                <li
                                    key={todo.id}
                                    className="list-group-item"
                                >
                                    <p className={`${todo.done && 'complete'}`} onClick={() => handleToggle(todo.id)}> {i + 1}. {todo.desc} </p>

                                    <button className="btn btn-danger" onClick={() => handleDelete(todo.id)}>Borrar</button>
                                </li>
                            ))
                        }
                    </ul> */}

                </div>

                <div className="col-5">

                    <h4>Agregar TODO</h4>
                    <hr />

                    <form onSubmit={handleSubmit}>

                        <input
                            type="text"
                            name="description"
                            placeholder="TODO..."
                            autoComplete="off"
                            className="form-control"
                            value={description}
                            onChange={handleInputChange}
                        />

                        <div className="d-grid gap-2">
                            <button className="btn btn-outline-primary mt-1" type="submit">Agregar</button>
                        </div>

                    </form>

                </div>

            </div>

        </div>
    )
}
