import React, { useEffect, useState } from 'react'
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Redirect
} from "react-router-dom";
import { firebase } from '../../firebase/firebaseConfig';
import { useDispatch } from 'react-redux';

import AuthRouter from './AuthRouter';
import JournalScreen from '../journal/JournalScreen';
import { login } from '../../actions/auth';
import { PublicRoute } from './PublicRoute';
import { PrivateRoute } from './PrivateRoute';

const AppRouter = () => {

    const dispatch = useDispatch();
    const [checking, setChecking] = useState(true);
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    console.log(isLoggedIn);

    useEffect( () => {

        firebase.auth().onAuthStateChanged((user) => {

            if (user?.uid) {
                dispatch(login(user.uid, user.displayName));
                setIsLoggedIn(true);
            }
            else {
                setIsLoggedIn(false);
            }

            // Una vez se autentica ya no se está revisando si el usuario está siendo autenticado.
            setChecking(false)
        })

    }, [dispatch, setChecking]);

    if (checking) {
        return (
            <div className="spinner round"></div>
        )
    }

    return (
        <Router>
            <div>
                <Switch>

                    <PublicRoute isLoggedIn={ isLoggedIn } component={ AuthRouter } />
                    <PrivateRoute isLoggedIn={ isLoggedIn } component={ JournalScreen } />

                    <Redirect to="/auth/login" />

                </Switch>
            </div>
        </Router>
    )
}

export default AppRouter;
