import React, { useEffect, useRef } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useForm } from '../../hooks/useForm';
import { NotesAppBar } from './NotesAppBar';
import { activeNote } from '../../actions/notes'

export const NoteScreen = () => {

    const dispatch = useDispatch();

    const { active: activeNote } = useSelector(state => state.notes);
    const [ formValues, handleInputChange, reset ] = useForm(activeNote);

    const { body, title } = formValues;

    const activeId = useRef( activeNote.id );

    useEffect( () => {

        if (activeNote.id !== activeId.current) {
            reset(activeNote);
            activeId.current = activeNote.id;
        }

    }, [activeNote, reset]);

    useEffect(() => {

        dispatch( activeNote( formValues.id, { ...formValues } ) ) ;

    }, [formValues])

    return (
        <div className="notes__main-content">
            <NotesAppBar />

            <div className="notes__content">
                <input
                    type="text"
                    placeholder="Put a title here"
                    className="notes__title-input"
                    autoComplete="off"
                    name="title"
                    value={ title }
                    onChange={ handleInputChange }
                />
                <textarea
                    placeholder="What happened today?"
                    className="notes__textarea"
                    name="body"
                    value={body}
                    onChange={ handleInputChange }
                >
                    </textarea>

                {
                    (activeNote.url) &&
                    <div className="notes__image">
                        <img
                            src="https://images.theconversation.com/files/125391/original/image-20160606-13080-s7o3qu.jpg?ixlib=rb-1.1.0&rect=273%2C0%2C2639%2C1379&q=45&auto=format&w=926&fit=clip"
                            alt="imagen"
                        />
                    </div>
                }



            </div>
        </div>
    )
}
