import React from 'react';
import { useSelector } from 'react-redux';
import { useForm } from '../../hooks/useForm';
import { NotesAppBar } from './NotesAppBar';

export const NoteScreen = () => {

    const { active: activeNote } = useSelector(state => state.notes);
    const [ formValues, handleInputChange ] = useForm(activeNote);

    const { body, title } = formValues;

    return (
        <div className="notes__main-content">
            <NotesAppBar />

            <div className="notes__content">
                <input
                    type="text"
                    placeholder="Put a title here"
                    className="notes__title-input"
                    autoComplete="off"
                    name="title"
                    value={ title }
                />
                <textarea
                    placeholder="What happened today?"
                    className="notes__textarea"
                    name="body"
                    value={ body }
                >
                    </textarea>

                <div className="notes__image">
                    <img
                        src="https://images.theconversation.com/files/125391/original/image-20160606-13080-s7o3qu.jpg?ixlib=rb-1.1.0&rect=273%2C0%2C2639%2C1379&q=45&auto=format&w=926&fit=clip"
                        alt="imagen"
                    />
                </div>
            </div>
        </div>
    )
}
