import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import moment from 'moment';
import Swal from 'sweetalert2';

import Modal from 'react-modal';
import DateTimePicker from 'react-datetime-picker';
import { uiCloseModal } from '../../actions/ui';
import { eventAddNew } from '../../actions/events';

const customStyles = {
    content: {
      top: '50%',
      left: '50%',
      right: 'auto',
      bottom: 'auto',
      marginRight: '-50%',
      transform: 'translate(-50%, -50%)',
    },
};

Modal.setAppElement('#root');

const now = moment().minutes(0).seconds(0).add(1, 'hours');
const end = moment(now).clone().add(1, 'hours');

const initEvent = {
    title: '',
    notes: '',
    startDate: now.toDate(),
    endDate: end.toDate()
}

export const CalendarModal = () => {

    const { modalOpen } = useSelector((state) => state.ui);
    const { activeEvent } = useSelector((state) => state.calendar);

    const dispatch = useDispatch();

    const [formValues, setFormValues] = useState(initEvent);

    const { notes, title, startDate, endDate } = formValues;

    useEffect(() => {

        if (activeEvent) {
            setFormValues(activeEvent);
        };

    }, [activeEvent, setFormValues]);

    const handleInputChange = ({ target }) => {
        setFormValues({
            ...formValues,
            [target.name]: target.value
        });
    };

    const [dateStart, setDateStart] = useState( now.toDate() );
    const [dateEnd, setDateEnd] = useState(end.toDate());
    const [titleValid, setTitleValid] = useState(true);

    const closeModal = () => {
        dispatch( uiCloseModal() );
        setFormValues(initEvent);
    }

    const handleStartDateChange = (evt) => {
        setDateStart(evt);
        setFormValues({
            ...formValues,
            startDate: evt
        });
    }

    const handleEndDateChange = (evt) => {
        setDateEnd(evt);
        setFormValues({
            ...formValues,
            endDate: evt
        });
    }

    const handleSubmitForm = (evt) => {

        evt.preventDefault();
        const momentStart = moment(startDate);
        const momentEnd = moment(endDate);

        if ( momentStart.isSameOrAfter(momentEnd) ) {
            return Swal.fire('Oops!', '¡La fecha final debe ser mayor a la de inicio!', 'error');
        };

        if (title.trim().length < 2) {
            return setTitleValid(false);
        }

        dispatch(eventAddNew({
            ...formValues,
            id: new Date().getTime(),
            user: {
                _id: '1234',
                name: 'yexhz'
            }
        }) );

        setTitleValid(true);
        closeModal();

    };

    return (
        <Modal
            isOpen={ modalOpen }
            onRequestClose={closeModal}
            style={customStyles}
            closeTimeoutMS={200}
            className="modal"
            overlayClassName="modal-fondo"
        >
            <h1> Nuevo evento </h1>
            <hr />
            <form className="container" onSubmit={ handleSubmitForm }>

                <div className="form-group">
                    <label>Fecha y hora inicio</label>
                    <DateTimePicker
                        onChange={handleStartDateChange}
                        value={dateStart}
                        className="form-control"
                    />
                </div>

                <div className="form-group">
                    <label>Fecha y hora fin</label>
                    <DateTimePicker
                        onChange={handleEndDateChange}
                        value={dateEnd}
                        minDate={ dateStart }
                        className="form-control"
                    />
                </div>

                <hr />

                <div className="form-group">
                    <label>Titulo y notas</label>
                    <input
                        type="text"
                        className={`form-control ${ !titleValid  && 'is-invalid' }`}
                        placeholder="Título del evento"
                        name="title"
                        autoComplete="off"
                        value={ title }
                        onChange={ handleInputChange }
                    />
                    <small id="emailHelp" className="form-text text-muted">Una descripción corta</small>
                </div>

                <div className="form-group">
                    <textarea
                        type="text"
                        className="form-control"
                        placeholder="Notas"
                        rows="5"
                        name="notes"
                        value={ notes }
                        onChange={ handleInputChange }
                    ></textarea>
                    <small id="emailHelp" className="form-text text-muted">Información adicional</small>
                </div>

                <button
                    type="submit"
                    className="btn btn-outline-primary btn-block"
                >
                    <i className="far fa-save"></i>
                    <span> Guardar</span>
                </button>

            </form>

        </Modal>
    )
}
