import { convertEvents } from "../helpers/convertEvents";
import { fetchToken } from "../helpers/fetch";
import { types } from "../types/types";

export const eventStartAddNew = (event) => {
    return async ( dispatch, getState ) => {

        const { uid, name } = getState().auth;

        try {
            const dbEvent = {
                ...event,
                start: event.startDate,
                end: event.endDate
            }

            const response = await fetchToken('events', dbEvent, 'POST');
            const body = await response.json();

            if (body.ok) {
                event.id = body.event.id
                event.user = {
                    _id: uid,
                    name: name
                }
                console.log(event);
                dispatch( eventAddNew(event) );
            }

        } catch (error) {
            console.log(error)
        }

    };
};

export const eventStartLoading = () => {
    return async (dispatch) => {

        try {
            const response = await fetchToken('events');
            const body = await response.json();

            const events = convertEvents(body.events);
           dispatch( eventsLoaded( events ) );

        } catch (error) {

        }

    };
};

export const eventStartUpdate = (event) => {
    return async (dispatch) => {

        try {
            console.log(event);
          //  const response = fetchToken(`events/${event.id}`);

        } catch (error) {

        };

    };
};

const eventAddNew = (event) => ({
    type: types.eventAddNew,
    payload: event
});

export const eventSetActive = (event) => ({
    type: types.eventSetActive,
    payload: event
});

export const eventClearActive = () => ({
    type: types.eventClearActive
});

export const eventUpdated = ( event ) => ({
    type: types.eventUpdated,
    payload: event
});

export const eventDeleted = () => ({
    type: types.eventDeleted
});

const eventsLoaded = (events) => ({ type: types.eventsLoaded, payload: events });
