const baseUrl = process.env.REACT_APP_API_URL;

const fetchNoToken = (endpoint, data, method = 'GET') => {

    const url = `${baseUrl}/${endpoint}`; // Ejemplo localhost:3100/api/auth

    if (method === 'GET') {

        // Retorna el producto de la petición si es GET.
        return fetch(url);

    } else {
        return fetch(url, {
            method,
            headers: {
                'Content-type': 'application/json'
            },
            body: JSON.stringify( data )
        });
    };

};

const fetchToken = (endpoint, data, method = 'GET') => {

    const url = `${baseUrl}/${endpoint}`; // Ejemplo localhost:3100/api/auth
    const token = localStorage.getItem('token') || '';

    if (method === 'GET') {

        // Retorna el producto de la petición si es GET.
        return fetch(url, {
            method,
            headers: {
                'x-token': token
            }
        });

    } else {
        return fetch(url, {
            method,
            headers: {
                'Content-type': 'application/json',
                'x-token': token
            },
            body: JSON.stringify( data )
        });
    };

};

export {
    fetchNoToken,
    fetchToken
};
